import React from "react";
import { useState, useEffect } from "react";
import "./Countries.css";

function Countries() {
    const [countries, setCountries] = useState([]);
    const [country, setCountry] = useState([]);
    const [load, setLoad] = useState(false);
    const url = "https://restcountries.com/v3.1/all";

    const fetchApi = async () => {
        const response = await fetch(url);
        const data = await response.json();
        setCountries(data);
        setLoad(false)
    }

    useEffect(() => {
        fetchApi();
    }, []);

    const countryData = (g) => {
        countries.forEach(con =>{
            if(g === con.name.official){
                console.log(con)
                setCountry(con);
                setLoad(true);
                console.log('done')
            }
        })
    }

    window.addEventListener('DOMContentLoaded',() => {
        const searchUI = document.getElementById("searchUI");

        searchUI.addEventListener("keyup", (e) => {
            let value = e.target.value;
            console.log(value);
            let countryName = document.querySelectorAll(".country_name");

            countryName.forEach(name => {
                console.log(name)
                if(name.innerText.toLowerCase().includes(value.toLowerCase())){
                    name.parentElement.parentElement.style.display = 'flex';
                }else{
                    name.parentElement.parentElement.style.display = 'none';
                }
            })
        })
    })

    window.addEventListener('DOMContentLoaded', () => {
        const region = document.querySelectorAll(".region-list");
        region.forEach((element) => {
            element.addEventListener('click', async(t) => {
                if(document.querySelector(".chosen") && document.querySelector(".chosen").id === t.target.id){
                    fetchApi()
                    document.querySelector(".chosen").classList.remove('chosen');
                }else{
                    const regionFetch = await fetch(`https://restcountries.com/v3.1/region/${t.target.id}`);
                    const regionData = await regionFetch.json();
                    setCountries(regionData);
                    if(!document.querySelector(".chosen")){
                        document.getElementById(`${t.target.id}`).classList.toggle('chosen');
                    }else{
                        document.querySelector(".chosen").classList.remove('chosen');
                        document.getElementById(`${t.target.id}`).classList.toggle('chosen');
                    }
                }
            })
        })
    })

    const currenciesc = (f) => {
        let currenciesList;
        for(let key in f){
            currenciesList = f[key].name;
        }
        return currenciesList;
    }

    const languages = (l) => {
        console.log(l)
        let languagesList = '';
        for(let key in l){
            languagesList = l[key] + ",";
        }
        return languagesList.slice(0, languagesList.length-2);
    }


    return (
            <>
                {load ? (
                    <div className="wrap">
                        <p>sddd</p>

                    </div>
                ): (
                    <div className="content__main">
                        {countries.map(
                            ({ name, population, region, capital, flags }) => (
                                <div class="content__main__block" onClick={() => {countryData(name.official)}}>
                                    <div class="content__main__block__image">
                                        <img src={flags.png} />
                                    </div>
                                    <div class="content__main__block__text">
                                        <h2 class="country_name">{name.official}</h2>
                                        <div class="content__main__block__text__about">
                                            <p class="population"><span class="bold-text">Population:</span> {population}</p>
                                            <p class="region"><span class="bold-text">Region:</span> {region}</p>
                                            <p class="capital"><span class="bold-text">Capital:</span><span className="countryCapital">{capital}</span></p>
                                        </div>
                                    </div>
                                </div>
                            )
                            )}
                    </div>
                )}
            </>
    )
}

export default Countries;
