import React from "react";
import { useEffect, useState } from "react"; 

function Countries() {
    const [countries, setCountries] = useState([]);

    const fetchApi = async () => {
        const url = "https://restcountries.com/v3.1/all";
        const response = await fetch(url);
        const data = await response.json();
        setCountries(data);
        console.log(countries);
    }

    useEffect(() => {
        fetchApi();
    }, [])

    const comma = (n) =>{
        let d = String(n);
        let g = "";
        let j = d.length%3;
        let o = d.slice(j, d.length);
        if(j === 0){
            for(let i = 0; i < d.length; i++){
                if(i !== 0 && i % 3 === 0){
                    g+= "," + d[i];
                } else{
                    g+= d[i];
                }
            }
        }else{
            g += d.slice(0, j);
            for(let x = 0; x < o.length; x++){
                if(x % 3 ===0){
                    g+= "," + o[x]
                }else{
                    g+=o[x]
                }

            }
        }
        return g;
    }

    return (
        <div className="content__main">
            {countries.map(
                ({name, flags, population, region, capital}) => {
                    <div class="content__main__block">
                        <div class="content__main__block__image">
                        <img src={flags.png} />
                    </div>
                    <div class="content__main__block__text">
                        <h2>{name.official}</h2>
                        <div class="content__main__block__text__about">
                            <p class="population"><span class="bold-text">Population:</span> {comma(population)}</p>
                            <p class="region"><span class="bold-text">Region:</span> {region}</p>
                            <p class="capital"><span class="bold-text">Capital:</span> {capital}</p>
                        </div>
                    </div>
                </div>
            })}
        </div>
    )

}

export default Countries;
