import React from "react";
import { useState, useEffect } from "react";
import "./Countries.css";

function Countries() {
    const [countries, setCountries] = useState([]);
    const url = "https://restcountries.com/v3.1/all";

    const fetchApi = async () => {
        const response = await fetch(url);
        const data = await response.json();
        setCountries(data);
        console.log(data);
    }

    useEffect(() => {
        fetchApi();
    }, []);

    window.addEventListener('DOMContentLoaded',() => {
        const searchUI = document.getElementById("searchUI");
        const countryName = document.querySelectorAll(".country_name");
        const countryCapital = document.querySelectorAll(".country_capital");

        searchUI.addEventListener("keyup", (e) => {
            const value = e.target;
        })
    })


    return (
            <div className="content__main">
                {countries.map(
                      ({ name, population, region, capital, flags }) => (
                        <div class="content__main__block">
                            <div class="content__main__block__image">
                            <img src={flags.png} />
                        </div>
                        <div class="content__main__block__text">
                            <h2 class="country_name">{name.official}</h2>
                            <div class="content__main__block__text__about">
                                <p class="population"><span class="bold-text">Population:</span> {population}</p>
                                <p class="region"><span class="bold-text">Region:</span> {region}</p>
                            <p class="capital"><span class="bold-text">Capital:</span><span className="countryCapital">{capital}</span></p>
                        </div>
                    </div>
                </div>
            ))}
        </div>
    )

}

export default Countries;
