import React from "react";
import { useState, useEffect } from "react";
import './Countries.css';

function Countries(){
    const [countries, setCountries] = useState([]);
    const blockReady = "";
    const url = "https://restcountries.com/v3.1/all";

    const fetchApi = async (n) => {
        const response = await fetch(url);
        const data = await response.json();
        setCountries(data)
    }

    useEffect(() => {
        fetchApi();
      }, []);


    return(
        <div className="content__main">
            {countries.map(
                ({name, population, region, capital, flags }) => (
                    `<div class="content__main__block" onclick="getInfo('${name.official}')" href="./more.html">
                        <div class="content__main__block__image">
                            <img src="${flags.png}" alt="${name.official} flag">
                        </div>
                        <div class="content__main__block__text">
                            <h2>${name.official}</h2>
                            <div class="content__main__block__text__about">
                                <p class="population"><span class="bold-text">Population:</span> ${population}</p>
                                <p class="region"><span class="bold-text">Region:</span> ${region}</p>
                                <p class="capital"><span class="bold-text">Capital:</span> ${capital}</p>
                            </div>
                        </div>
                    </div>`
                )
            )}
        </div>
    )
}

export default Countries;
