import React from "react";
import { useState, useEffect } from "react";
import './Countries.css';

function Countries(){
    const [countries, setCountries] = useState([]);
    const blockReady = "";
    const url = "https://restcountries.com/v3.1/all";

    const fetchApi = async (n) => {
        const response = await fetch(url);
        const data = await response.json();
        setCountries(data)
    }

    useEffect(() => {
        fetchApi();
      }, []);


    return(
        <div className="content__main">
            {countries.map(
                ({name, population, region, capital, flags }) => (
                    <div className="content__main__block">
                        <div className="content__main__block__image">
                            <img src={flags.png}/>
                        </div>
                        <div className="content__main__block__text">
                            <h2>{name.official}</h2>
                            <div className="content__main__block__text__about">
                                <p className="population"><span className="bold-text">Population:</span> ${population}</p>
                                <p class="region"><span class="bold-text">Region:</span> ${region}</p>
                                <p class="capital"><span class="bold-text">Capital:</span> ${capital}</p>
                            </div>
                        </div>
                    </div>
                )
            )}
        </div>
    )
}

export default Countries;
