import React from "react";
import { useState } from "react";
import './Region.css';
import Countries from "../Countries/Countries";"../Countries/Countries";

function Region() {

    const openRegion = () => {
        document.querySelector(".content__region__block__list").classList.toggle("showRegionMenu");
        console.log('cc')
    }

    const fetchRegion = (e) => {
        const url = `https://restcountries.com/v3.1/region/${e}`;
        fetch(url);
    }

    return (
        <div class="content__region">
                <div class="content__region__op-cl" onClick={() => {document.querySelector(".content__region__block__list").classList.toggle("showRegionMenu")}}>
                    <h3>Filter by Region</h3>
                    <svg xmlns="http://www.w3.org/2000/svg" class="ionicon" viewBox="0 0 512 512" width='16px' height='16px'><title>Chevron Down</title><path fill="none" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="48" d="M112 184l144 144 144-144"/></svg>
                </div>
                <div class="content__region__block">
                    <ul class="content__region__block__list">
                    <li >Africa</li>
                    <li >America</li>
                    <li >Asia</li>
                    <li >Europe</li>
                    <li >Oceania</li>
                </ul>
            </div>
        </div>
    )
}

export default Region;
