import React from "react";
import { useState, useEffect } from "react";

function Countries(){
    const [countries, setCountries] = useState([]);
    const blockReady = "";
    const url = "https://restcountries.com/v3.1/all";

    const fetchApi = async (n) => {
        const response = await fetch(url);
        const data = await response.json();
        setCountries(data)
        console.log(countries);
        getPage()
    }

    const getPage =  () => {
        countries.forEach(country => {
            blockReady+= ' ' + `<a class="content__main__block" onclick="getInfo('${country.name.official}')" href="./more.html">
                    <div class="content__main__block__image">
                        <img src="${country.flags.png}" alt="${country.name.official} flag">
                    </div>
                    <div class="content__main__block__text">
                        <h2>${country.name.official}</h2>
                        <div class="content__main__block__text__about">
                            <p class="population"><span class="bold-text">Population:</span> ${country.population}</p>
                            <p class="region"><span class="bold-text">Region:</span> ${country.region}</p>
                            <p class="capital"><span class="bold-text">Capital:</span> ${country.capital}</p>
                        </div>
                    </div>
                </a>`
        })
        console.log(blockReady)
    }
    useEffect(() => {
        fetchApi();
      }, []);

}

export default Countries;
